import sqlite3db_path = "customer.db"conn = sqlite3.connect(db_path)cur = conn.cursor()# Check if column existscur.execute("PRAGMA table_info(accounts_receivable);")columns = [info[1] for info in cur.fetchall()]if 'current_balance' not in columns:    cur.execute("ALTER TABLE accounts_receivable ADD COLUMN current_balance REAL;")    print("Added 'current_balance' column.")else:    print("'current_balance' column already exists.")# Set all current_balance values to total_amount if they are NULLcur.execute("UPDATE accounts_receivable SET current_balance = total_amount WHERE current_balance IS NULL;")conn.commit()print("Initialized current_balance for existing rows.")conn.close()print("Done.")