import sqlite3DATABASE_FILE = "customer.db"# List of columns to add with their types and optional defaultnew_columns = [    ('products', 'TEXT'),    ('description', 'TEXT'),    ('number_of_items', 'TEXT'),    ('price', 'TEXT'),    ('discount', 'TEXT'),    ('subtotal', 'REAL'),    ('vat', 'REAL'),    ('total_amount', 'REAL'),    ('paid_status', "TEXT DEFAULT 'UNPAID'"),    ('current_balance', 'REAL')]def add_columns_if_missing(dbfile, table, columns):    with sqlite3.connect(dbfile) as conn:        cur = conn.cursor()        # Get list of existing columns        cur.execute(f"PRAGMA table_info({table});")        existing = [row[1] for row in cur.fetchall()]        for col_name, col_type in columns:            if col_name not in existing:                print(f"Adding column: {col_name}")                cur.execute(f"ALTER TABLE {table} ADD COLUMN {col_name} {col_type};")            else:                print(f"Column already exists: {col_name}")        conn.commit()    print("Done.")if __name__ == "__main__":    add_columns_if_missing(DATABASE_FILE, "accounts_receivable", new_columns)