def init_db():    with closing(sqlite3.connect(DATABASE_FILE)) as conn:        with conn:            # Create the table if it does not exist            conn.execute('''                CREATE TABLE IF NOT EXISTS accounts_receivable (                    id INTEGER PRIMARY KEY AUTOINCREMENT                    -- Other fields will be added below if missing                )            ''')            # List of columns to ensure exist (add or skip)            columns_to_add = [                ('invoice_number', 'TEXT'),                ('company_name', 'TEXT'),                ('invoice_date', 'TEXT'),                ('due_date', 'TEXT'),                ('products', 'TEXT'),                ('description', 'TEXT'),                ('number_of_items', 'TEXT'),                ('price', 'TEXT'),                ('discount', 'TEXT'),                ('subtotal', 'REAL'),                ('vat', 'REAL'),                ('total_amount', 'REAL'),                ('paid_status', "TEXT DEFAULT 'UNPAID'"),                ('current_balance', 'REAL')            ]            # Get current columns            cur = conn.execute("PRAGMA table_info(accounts_receivable);")            existing_cols = [row[1] for row in cur.fetchall()]            # Add columns if missing            for col, typ in columns_to_add:                if col not in existing_cols:                    try:                        conn.execute(f"ALTER TABLE accounts_receivable ADD COLUMN {col} {typ};")                        print(f"Added column: {col}")                    except Exception as e:                        print(f"Could not add column {col}: {e}")            # (You can do the same for bill_to_info, receipts, etc., if needed)    print("Database initialized (with new fields if needed).")